import React from 'react'

const Videos = ({ videos }) => {
  console.log(videos)

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: '2-digit', day: '2-digit' };
    const formattedDate = new Date(dateString).toLocaleDateString('ko-KR', options).replace(/\./g, '-');
    return formattedDate;
  };


  return (
    <section id="view" className="section">
      <h2>오늘의 추천 영상</h2>
      <div className="view__wrap">
        {videos.map((video, key) => (
          <div className="view__box" key={key}>
            <div className="thumb">
              <a href="/" style={{ backgroundImage: `url(${video.snippet.thumbnails.high.url})` }}>
                {/* <img src={video.snippet.thumbnails.high.url} alt={video.snippet.title} /> */}
              </a>
            </div>
            <div className="desc">
              <div className="title">
                <a href="/">{video.snippet.title}</a>
              </div>
              <div className="info">
                <a href="/" className="author">{video.snippet.channelTitle}</a>
                <span>{formatDate(video.snippet.publishedAt)}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  )
}

export default Videos
